package com.week2labexercise.com;
import java.util.*;
public class Multiply2Numbers {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		System.out.println("Hi There.. Welcome to the calculator of multiplying 2 numbers");
		Scanner sc =  new Scanner(System.in);
		System.out.println("Please enter the first number");
		int a = sc.nextInt();
		System.out.println("Please enter the second number");
		int b = sc.nextInt();
		int sum = a*b;
		System.out.println("Please find the result of entered 2 numbers  " + sum);
		

	}

}
